INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-06-20', 'SingleStore Demo Day', 'Dubai', 'I got to see the latest SingleStore release as well as meet other Developers invited to the Demo day.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-06-25', 'Google Developer Event', 'London', 'As speaker at the Google Developer event in London, I was able to share my experiences in the field.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-06-28', 'Visiting Old Office', 'London', 'Today I visited the place where I got my very first role as a Developer. It was great to see so many familiar faces working there still.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-06-29', 'Best Friends Birthday', 'London', 'I celebrated my best friends birthday at a restaurant in the middle of Soho.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-01', 'FreeCodeCamp London Meetup', 'London', 'As a member of the FreeCodeCamp team, I was invited to their London meetup event. These are very rare so it was very lucky that I was in town.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-02', 'Day In The Park', 'London', 'Today was a day of relaxation. I spent it with some friends in Battersea park.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-03', 'Microsoft Pitch Day', 'London', 'Microsoft Pitch day was an incredible opportunity to meet some amazing minds and see what they were up to.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-04', 'Huawei Coding Competition', 'Switzerland', 'I got to visit the Huawei coding competition and it was a great experience. The team was very friendly and welcoming. Overall, I was proud to be a part of this incredible initiatve.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-06', 'United Nations Visit', 'Switzerland', 'It was an honour to visit the United Nations in Geneva and hear pitches from all the Huawei competition finalists.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-07', 'Coffee Shop Opening', 'Dubai', 'Today I opened by coffee shop for remote workers! It is called Homebrew as a nod to all my fellow Developers out there.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-08', 'Photoshoot Of Homebrew Cafe', 'Dubai', 'Today we took a photoshoot of the Homebrew cafe. It was a great experience! I had a very talented photograper and was able to capture some amazing shots.');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-07-09', 'Our First 100 Coffees Sold', 'Dubai', 'We sold our very first 100 coffees and it was a very proud day!');
INSERT INTO memories (date_memory, memory_title, location, memory) VALUES ('2024-06-11', 'New Chef Joins The Team', 'Dubai', 'A new chef has joined our Homebrew Coffee Shop team. They have already prepared an amazing menu of sandwiches and bagels.');
